import { useState } from 'react';
import { useNavigate } from '@remix-run/react';
import type { UploadFile } from 'antd';
import {
  message,
  Upload,
  Button,
  Form,
  Radio,
  Checkbox,
  Image,
  Flex,
} from 'antd';
import { WithNavigation } from '~/components/WithNavigation';
import { uploadImage } from '~/api';
import { UploadChangeParam } from 'antd/es/upload';

const boxStyle: React.CSSProperties = {
  marginTop: '20px',
};

export default function Scan() {
  const [previewImage, setPreviewImage] = useState('');
  const [form] = Form.useForm();
  const [loading, setLoading] = useState(false);

  const handleChange = (info: UploadChangeParam) => {
    const { status } = info.file;
    if (status === 'done') {
      message.success(`${info.file.name} file uploaded successfully.`);
    } else if (status === 'error') {
      message.error(`${info.file.name} file upload failed.`);
    } else if (status === 'removed') {
      // Handle removal if necessary
    }

    // Preview image before uploading
    handlePreview(info.file);
  };

  const handlePreview = async (file: UploadFile<unknown>) => {
    const reader = new FileReader();
    reader.readAsDataURL(file.originFileObj);
    reader.onload = () => {
      setPreviewImage(reader.result);
    };
  };

  const uploadImageRequest = async ({ file, onSuccess, onError }) => {
    const formData = new FormData();
    formData.append('files', file);

    try {
      const response = await uploadImage(formData);
      console.log('1', response);

      if (response.message) {
        onSuccess('Ok');
      } else {
        onError('Error');
      }
    } catch (e) {
      console.error('Error uploading file:', e);
      onError(e);
    }
  };

  return (
    <WithNavigation>
      <Flex
        gap='middle'
        vertical
        justify='center'
        align='center'
        style={boxStyle}
      >
        <Form form={form}>
          <Form.Item>
            <Image
              width={300}
              height={300}
              src={previewImage ?? 'error'}
              fallback='data:image/png;base64,'
            />
          </Form.Item>
          {!previewImage ? (
            <Form.Item>
              <Upload
                onChange={handleChange}
                customRequest={uploadImageRequest}
              >
                <Button>Click to Upload image</Button>
              </Upload>
            </Form.Item>
          ) : (
            <>
              <Form.Item label='Model' name='model'>
                <Radio.Group value='resnet-50'>
                  <Radio.Button value='resnet-50'>Resnet 50</Radio.Button>
                  <Radio.Button value='resnet-101'>Resnet 101</Radio.Button>
                  <Radio.Button value='resnet-152'>Resnet 152</Radio.Button>
                  <Radio.Button value='vgg-16'>VGG 16</Radio.Button>
                  <Radio.Button value='vgg-19'>VGG 19</Radio.Button>
                </Radio.Group>
              </Form.Item>

              <Form.Item label='Activation function' name='activation-function'>
                <Radio.Group value='resnet-50'>
                  <Radio.Button value='relu'>Relu</Radio.Button>
                  <Radio.Button value='softmax'>Soft-max</Radio.Button>
                  <Radio.Button value='sigmoid'>Sigmoid</Radio.Button>
                  <Radio.Button value='tanh'>Tanh</Radio.Button>
                </Radio.Group>
              </Form.Item>

              <Form.Item label='Help improve model'>
                <Checkbox>Allow image to be used for training</Checkbox>
              </Form.Item>
            </>
          )}
        </Form>
      </Flex>
    </WithNavigation>
  );
}
